DELIMITER $$
CREATE TRIGGER `crea_collegamento_segnalazione` AFTER INSERT ON `proposta_class` FOR EACH ROW IF (SELECT COUNT(*)
	FROM proposta_class p
	WHERE p.id_segn = new.id_segn) >= 5 THEN
    IF (SELECT p.nome_spec
        FROM proposta_class p
        WHERE p.id_segn = '9'
        GROUP BY p.nome_spec
        HAVING COUNT(*) > all (
            SELECT COUNT(*)
            FROM proposta_class c
            WHERE c.id_segn = '9'
            AND c.nome_spec <> p.nome_spec
            GROUP BY c.nome_spec
        )
       ) IS NOT NULL THEN
    	UPDATE segnalazione s
		SET s.nome_spec = (SELECT p2.nome_spec
                            FROM proposta_class p2
                            WHERE p2.id_segn = '9'
                            GROUP BY p2.nome_spec
                            HAVING COUNT(*) > all (
                                SELECT COUNT(*)
                                FROM proposta_class c2
                                WHERE c2.id_segn = '9'
                                AND c2.nome_spec <> p2.nome_spec
                                GROUP BY c2.nome_spec
                            )
                           )
        WHERE s.id_segn = new.id_segn;
    END IF;
END IF
$$
DELIMITER ;

