--
-- Trigger `segnalazione`
--
DELIMITER $$
CREATE TRIGGER `agg_punteggi_seg` BEFORE UPDATE ON `segnalazione` FOR EACH ROW IF old.nome_spec IS NULL AND new.nome_spec IS NOT NULL THEN
	UPDATE premium u
    SET u.class_corrette = u.class_corrette + 1
    WHERE u.nome IN (
    	SELECT p.nome_utente
        FROM proposta_class p
        WHERE p.id_segn = old.id_segn
        AND p.nome_spec = new.nome_spec
    );
    UPDATE premium u
    SET u.class_errate = u.class_errate + 1
    WHERE u.nome IN (
    	SELECT p.nome_utente
        FROM proposta_class p
        WHERE p.id_segn = old.id_segn
        AND p.nome_spec <> new.nome_spec
    );
    UPDATE premium u
    SET u.affidabilita = (u.class_corrette / u.class_errate)
    WHERE u.nome IN (
    	SELECT p.nome_utente
        FROM proposta_class p
        WHERE p.id_segn = old.id_segn
        AND p.nome_spec <> new.nome_spec
    );
END IF
$$
DELIMITER ;

